(function(){
  const $ = id => document.getElementById(id);
  const toast = (m,t=1100)=>{ const e=document.createElement('div'); e.className='toast'; e.textContent=m; document.body.appendChild(e); setTimeout(()=>e.remove(),t); };
  const STORE='overlay_gui_fixed_v1';
  const genId=()=> 'id_'+Math.random().toString(36).slice(2,9);
  let state={pos:{x:20,y:20},tabs:[{id:genId(),title:'Main',actions:[{id:genId(),label:'Hello',type:'alert',payload:'Halo!'}]}],active:0,minimized:false};
  try{const raw=localStorage.getItem(STORE);if(raw)state=JSON.parse(raw);}catch(e){}
  const panelWrap=$('panelWrap'),panel=$('panel'),hdr=$('hdr');
  const tabsEl=$('tabs'),body=$('body'),editor=$('editor');
  const labelInput=$('labelInput'),actionType=$('actionType'),addBtn=$('addBtn');
  const addTabBtn=$('addTabBtn'),minBtn=$('minBtn'),closeBtn=$('closeBtn'),saveBtn=$('saveBtn'),inspectBtn=$('inspectBtn');
  panelWrap.style.transform=`translate(${state.pos.x}px,${state.pos.y}px)`;
  let dragging=false,startPointer={x:0,y:0},startPos={x:0,y:0};
  hdr.addEventListener('pointerdown',e=>{e.preventDefault();hdr.setPointerCapture(e.pointerId);dragging=true;panel.classList.add('dragging');startPointer={x:e.clientX,y:e.clientY};startPos={x:state.pos.x,y:state.pos.y};});
  document.addEventListener('pointermove',e=>{if(!dragging)return;e.preventDefault();const nx=startPos.x+(e.clientX-startPointer.x);const ny=startPos.y+(e.clientY-startPointer.y);state.pos.x=nx;state.pos.y=ny;panelWrap.style.transform=`translate(${nx}px,${ny}px)`;});
  document.addEventListener('pointerup',e=>{if(!dragging)return;dragging=false;panel.classList.remove('dragging');try{localStorage.setItem(STORE,JSON.stringify(state));}catch(e){}});
  function render(){
    if(state.minimized){tabsEl.style.display='none';body.style.display='none';editor.style.display='none';minBtn.textContent='â¬†';}
    else{tabsEl.style.display='';body.style.display='';editor.style.display='';minBtn.textContent='â€”';}
    tabsEl.innerHTML='';state.tabs.forEach((t,i)=>{const b=document.createElement('button');b.className='tab'+(i===state.active?' active':'');b.textContent=t.title;b.addEventListener('click',()=>{state.active=i;render();});tabsEl.appendChild(b);});
    body.innerHTML='';const cur=state.tabs[state.active]||state.tabs[0];const row=document.createElement('div');row.className='panel-row';(cur.actions||[]).forEach(act=>{const btn=document.createElement('button');btn.className='action-btn';btn.textContent=act.label;btn.addEventListener('click',()=>runAction(act));row.appendChild(btn);});if((cur.actions||[]).length===0){const h=document.createElement('div');h.textContent='(No actions yet)';row.appendChild(h);}body.appendChild(row);
  }
  function runAction(act){try{if(act.type==='alert')alert(act.payload||act.label);else if(act.type==='log')console.log(act.payload||act.label);else if(act.type==='copy'){const txt=act.payload||act.label;navigator.clipboard.writeText(txt).then(()=>toast('Copied'));}else if(act.type==='custom'){(new Function(act.payload||''))();}}catch(e){}}
  addBtn.addEventListener('click',()=>{const label=labelInput.value.trim();if(!label){toast('Masukkan label');return;}const type=actionType.value;let payload='';if(type==='alert')payload=prompt('Pesan alert:','Hello')||'';else if(type==='log')payload=prompt('Pesan console.log:',label)||'';else if(type==='copy')payload=prompt('Text untuk copy:',label)||'';else if(type==='custom')payload=prompt('Custom JS:','console.log(\"hi\")')||'';const cur=state.tabs[state.active]||state.tabs[0];cur.actions=cur.actions||[];cur.actions.push({id:genId(),label,type,payload});labelInput.value='';save();render();});
  addTabBtn.addEventListener('click',()=>{const name=prompt('Nama tab baru:','Tools');if(!name)return;state.tabs.push({id:genId(),title:name,actions:[]});state.active=state.tabs.length-1;save();render();});
  minBtn.addEventListener('click',()=>{state.minimized=!state.minimized;save();render();});
  closeBtn.addEventListener('click',()=>{panelWrap.remove();toast('Panel closed');});
  saveBtn.addEventListener('click',()=>{save();});
  function save(){try{localStorage.setItem(STORE,JSON.stringify(state));toast('Saved');}catch(e){}}
  render();
})();
